    def ask_question(self, chat_id, message):  
        if self.current_question_index < len(self.questions):  
            question = self.questions[self.current_question_index]  
            options = "\n".join(self.options[self.current_question_index])
            self.bot.send_message(chat_id, f'–í–æ–ø—Ä–æ—Å {self.current_question_index + 1}: {question}\n{options}')  
        else:  
            self.calculate_result(chat_id, message)
            

